package ch02;

public class variableTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age; //변수 선언(노란줄은 변수 선언 후 한번도 사용하지 않았기 때문)
		
		age = 30; // 대입연산자 left value = right value
		System.out.println(age);
		
		age = 10;
		System.out.println(age);
		
		//초기화 방법 int age = 10;
		//여러 변수 선언 int age, count;
	}

}
